/*! html2pdf local stub | Fallback para impressão nativa (sem CDN) */
(function(){
  function factory(){
    var api = {
      from: function(node){ this.__node = node; return this; },
      set: function(opt){ this.__opt = opt||{}; return this; },
      save: function(){
        return new Promise(function(resolve){
          try {
            // Usa impressão nativa do navegador; CSS de @media print deve estar configurado.
            window.print();
          } catch(e) { console && console.warn && console.warn(e); }
          resolve();
        });
      }
    };
    function wrapper(){ return Object.create(api); }
    // suporte a chamada direta: html2pdf().from(...).set(...).save()
    wrapper.from = function(node){ return wrapper().from(node); };
    wrapper.set = function(opt){ return wrapper().set(opt); };
    wrapper.save = function(){ return wrapper().save(); };
    return wrapper;
  }
  var h2p = factory();
  if (typeof window !== 'undefined'){ window.html2pdf = h2p; }
})();